function renderHomeIntro(lang) {
  const container = document.getElementById("home-intro");
  if (!container || !homeIntro[lang]) return;

  const t = homeIntro[lang];

  container.innerHTML = `
    <h2>${t.title}</h2>
    <p>${t.p1}</p>
    <p>${t.p2}</p>
    <p>${t.p3}</p>
    <p>${t.p4}</p>
    <p>${t.p5}</p>
  `;
}




